let pageInstances = []; // 当前所有的页面实例
let appInstance = null;
/**
 * 为实例上的方法添加装饰器（在运行对应方法前会运行设定的回调）
 * @instance 实例对象
 * @methodName 添加装饰器的方法名
 * @callback 回调函数
 */
function addDecorator(instance, methodName, callback) {
  // TODO：这里只是简单的修改了原有的函数，多层装饰器时需要优化
  const originalMethod = instance[methodName]
  instance[methodName] = function () {
    callback && callback.apply(this, arguments)
    originalMethod && originalMethod.apply(this, arguments)
  }
}

/**
 * 为单个方法添加装饰器（在方法调用前调用装饰器）
 * @return 带装饰器的方法
 */
function addDecorator2(f, decorator) {
  return function () {
    decorator.apply(this, arguments)
    f && f.apply(this, arguments)
  }
}


/**
 * onLoad前执行
 * 1. 绑定全局的方法
 */
function beforePageOnLoad() {
  // 需要实例作为上下文
  this.setData({
    $globalData: appInstance.globalData
  })
  pageInstances.push(this)
  // this.$global = {
  //   // TODO：修改方法让全局设置支持回调
  //   setData: globalSetData
  // }
}

/**
 * onUnload前执行
 * 1.从pageInstances中移除当前页面实例
 */
function beforePageOnUnload() {
  let idx = pageInstances.indexOf(this)
  if (idx !== -1) {
    pageInstances.splice(idx, 1)
  }
}

/**
 * 用于data的深拷贝，不可用于函数、时间、正则等
 */
function deepCopyData(o) {
  return JSON.parse(JSON.stringify(o))
}

function globalSetData(o) {
  // 一个flag，代表是否已经更新了 appInstance.globalData 的值并使之与所有页面的$globalData同步
  let globalDataHasModify = false
  let o2 = {}
  for (let key in o) {
    o2['$globalData.' + key] = o[key]
  }
  pageInstances.forEach(pageItem => {
    // XXX:根据小程序文档中描述，不应该对非前台的页面进行setData
    // 这里可以把setData优化成把o2的值暂存起来
    // 在页面onShow时才执行setData的操作
    pageItem.setData(o2)

    // 更新 appInstance.globalData 的值
    if (!globalDataHasModify) {
      appInstance.globalData = deepCopyData(pageItem.data.$globalData)
      globalDataHasModify = true
    }
  })
}

// 判断当前用户有没有权限
function hasPermission(str,{tip}={}){
  console.assert(str !== undefined, '需要传入权限对应的字符串')
  let permit = appInstance.globalData.permissions[str] || false;
  if (tip && !permit) wx.showToast({
    title: '黄金会员可使用此功能，会员购买或升级请联系龙小姐：18620880485',
    icon: 'none'
  })
  return permit;
}

// App
const _App = addDecorator2(App, function (appOption) {
  addDecorator(appOption, 'onLaunch', function () {
    // XXX：如果在onLaunch阶段修改了globalData会有点问题
    // wx.app = this
    appInstance = this
  })
})

// Page
const _Page = addDecorator2(Page, function (option) {
  option.data = option.data || {}
  // 装饰页面的onLoad方法
  option.$hasPermission = hasPermission
    // TODO：修改方法让全局设置支持回调
  option.$globalSetData = globalSetData
  option.onLoad = addDecorator2(option.onLoad, beforePageOnLoad)
  option.onUnload = addDecorator2(option.onUnload, beforePageOnUnload)
})

module.exports = {
  App: _App,
  Page: _Page,
  globalSetData
}

App = _App
Page = _Page

function isValidUrl(url) {
  return /(ht|f)tp(s?):\/\/([^ \\/]*\.)+[^ \\/]*(:[0-9]+)?\/?/.test(url);
}

module.exports = {
  isValidUrl,
};

const formatTime = (date,splitSymbol) => {
  const year = date.getFullYear()
  const month = date.getMonth() + 1
  const day = date.getDate()
  const hour = date.getHours()
  const minute = date.getMinutes()
  const second = date.getSeconds()

  return [year, month, day].map(formatNumber).join(splitSymbol||'/') + ' ' + [hour, minute, second].map(formatNumber).join(':')
}

const formatDate = date => {
  const year = date.getFullYear()
  const month = date.getMonth() + 1
  const day = date.getDate()
  return [year, month, day].map(formatNumber).join('-')
}

const formatNumber = n => {
  n = n.toString()
  return n[1] ? n : '0' + n
}

//把完整的地址截成“xx市”
const address2City = address=> {
  const reg1 = /[^省区市()（）]+[市州]/
  if (reg1.test(address)) {
    return reg1.exec(address)[0];
  }
  else {
    return null
  }
}

//获取页面栈中的上一页
const getPrevPage = function(){
  let pages = getCurrentPages()
  let prevPage = pages[pages.length - 2]
  if (prevPage) {
    return prevPage;
  }else{
    return null;
  }  
}

//传入一个value，将
const valueTrim = function(valueObject){
  for(let attr in valueObject){
    if (typeof valueObject[attr] === "string") {
      valueObject[attr] = valueObject[attr].trim();
    }
  }
}

/**
 * 传入一个数组和间隔
 * 将这个数组等分后返回
 * 示例输入：[1,2,3] 2
 * 示例输出：[ [1,2], [3] ]
 */
const splitArray = function (originArray, interval) {
  let newArray = []
  for (let i = 0, len = originArray.length; i < len; i += interval) {
    newArray.push(originArray.slice(i, i + interval))
  }
  return newArray
}

module.exports = {
  formatTime: formatTime,
  formatDate: formatDate,
  address2City: address2City,
  getPrevPage: getPrevPage,
  valueTrim,
  splitArray
}

/**
 * 
 * htmlParser改造自: https://github.com/blowsie/Pure-JavaScript-HTML5-Parser
 * 
 * author: Di (微信小程序开发工程师)
 * organization: WeAppDev(微信小程序开发论坛)(http://weappdev.com)
 *               垂直微信小程序开发交流社区
 * 
 * github地址: https://github.com/icindy/wxParse
 * 
 * for: 微信小程序富文本解析
 * detail : http://weappdev.com/t/wxparse-alpha0-1-html-markdown/184
 */
// Regular Expressions for parsing tags and attributes
var startTag = /^<([-A-Za-z0-9_]+)((?:\s+[a-zA-Z_:][-a-zA-Z0-9_:.]*(?:\s*=\s*(?:(?:"[^"]*")|(?:'[^']*')|[^>\s]+))?)*)\s*(\/?)>/,
	endTag = /^<\/([-A-Za-z0-9_]+)[^>]*>/,
	attr = /([a-zA-Z_:][-a-zA-Z0-9_:.]*)(?:\s*=\s*(?:(?:"((?:\\.|[^"])*)")|(?:'((?:\\.|[^'])*)')|([^>\s]+)))?/g;

// Empty Elements - HTML 5
var empty = makeMap("area,base,basefont,br,col,frame,hr,img,input,link,meta,param,embed,command,keygen,source,track,wbr");

// Block Elements - HTML 5
var block = makeMap("a,address,code,article,applet,aside,audio,blockquote,button,canvas,center,dd,del,dir,div,dl,dt,fieldset,figcaption,figure,footer,form,frameset,h1,h2,h3,h4,h5,h6,header,hgroup,hr,iframe,ins,isindex,li,map,menu,noframes,noscript,object,ol,output,p,pre,section,script,table,tbody,td,tfoot,th,thead,tr,ul,video");

// Inline Elements - HTML 5
var inline = makeMap("abbr,acronym,applet,b,basefont,bdo,big,br,button,cite,del,dfn,em,font,i,iframe,img,input,ins,kbd,label,map,object,q,s,samp,script,select,small,span,strike,strong,sub,sup,textarea,tt,u,var");

// Elements that you can, intentionally, leave open
// (and which close themselves)
var closeSelf = makeMap("colgroup,dd,dt,li,options,p,td,tfoot,th,thead,tr");

// Attributes that have their values filled in disabled="disabled"
var fillAttrs = makeMap("checked,compact,declare,defer,disabled,ismap,multiple,nohref,noresize,noshade,nowrap,readonly,selected");

// Special Elements (can contain anything)
var special = makeMap("wxxxcode-style,script,style,view,scroll-view,block");

function HTMLParser(html, handler) {
	var index, chars, match, stack = [], last = html;
	stack.last = function () {
		return this[this.length - 1];
	};

	while (html) {
		chars = true;

		// Make sure we're not in a script or style element
		if (!stack.last() || !special[stack.last()]) {

			// Comment
			if (html.indexOf("<!--") == 0) {
				index = html.indexOf("-->");

				if (index >= 0) {
					if (handler.comment)
						handler.comment(html.substring(4, index));
					html = html.substring(index + 3);
					chars = false;
				}

				// end tag
			} else if (html.indexOf("</") == 0) {
				match = html.match(endTag);

				if (match) {
					html = html.substring(match[0].length);
					match[0].replace(endTag, parseEndTag);
					chars = false;
				}

				// start tag
			} else if (html.indexOf("<") == 0) {
				match = html.match(startTag);

				if (match) {
					html = html.substring(match[0].length);
					match[0].replace(startTag, parseStartTag);
					chars = false;
				}
			}

			if (chars) {
				index = html.indexOf("<");
				var text = ''
				while (index === 0) {
                                  text += "<";
                                  html = html.substring(1);
                                  index = html.indexOf("<");
				}
				text += index < 0 ? html : html.substring(0, index);
				html = index < 0 ? "" : html.substring(index);

				if (handler.chars)
					handler.chars(text);
			}

		} else {

			html = html.replace(new RegExp("([\\s\\S]*?)<\/" + stack.last() + "[^>]*>"), function (all, text) {
				text = text.replace(/<!--([\s\S]*?)-->|<!\[CDATA\[([\s\S]*?)]]>/g, "$1$2");
				if (handler.chars)
					handler.chars(text);

				return "";
			});


			parseEndTag("", stack.last());
		}

		if (html == last)
			throw "Parse Error: " + html;
		last = html;
	}

	// Clean up any remaining tags
	parseEndTag();

	function parseStartTag(tag, tagName, rest, unary) {
		tagName = tagName.toLowerCase();

		if (block[tagName]) {
			while (stack.last() && inline[stack.last()]) {
				parseEndTag("", stack.last());
			}
		}

		if (closeSelf[tagName] && stack.last() == tagName) {
			parseEndTag("", tagName);
		}

		unary = empty[tagName] || !!unary;

		if (!unary)
			stack.push(tagName);

		if (handler.start) {
			var attrs = [];

			rest.replace(attr, function (match, name) {
				var value = arguments[2] ? arguments[2] :
					arguments[3] ? arguments[3] :
						arguments[4] ? arguments[4] :
							fillAttrs[name] ? name : "";

				attrs.push({
					name: name,
					value: value,
					escaped: value.replace(/(^|[^\\])"/g, '$1\\\"') //"
				});
			});

			if (handler.start) {
				handler.start(tagName, attrs, unary);
			}

		}
	}

	function parseEndTag(tag, tagName) {
		// If no tag name is provided, clean shop
		if (!tagName)
			var pos = 0;

		// Find the closest opened tag of the same type
		else {
			tagName = tagName.toLowerCase();
			for (var pos = stack.length - 1; pos >= 0; pos--)
				if (stack[pos] == tagName)
					break;
		}
		if (pos >= 0) {
			// Close all the open elements, up the stack
			for (var i = stack.length - 1; i >= pos; i--)
				if (handler.end)
					handler.end(stack[i]);

			// Remove the open elements from the stack
			stack.length = pos;
		}
	}
};


function makeMap(str) {
	var obj = {}, items = str.split(",");
	for (var i = 0; i < items.length; i++)
		obj[items[i]] = true;
	return obj;
}

module.exports = HTMLParser;

/**
 * 这是店铺的分享图
 */
const imgUrl = getApp().globalData.imgUrl
// rate是放大倍率，如果放大倍率过小的话二维码像素过低会模糊，倍率太大图片渲染很慢
