let offsetLeft = 0 * rate;
let offsetTop = 0 * rate;
//下面的详情的偏移量（未做）
let detailOffsetLeft = 0 * rate;
let detailOffsetTop = 0 * rate;
//下面的详情的间距
let detailMargin = 50 * rate;

let shareMsgOffsetTop = 400*rate

