/**
 * 这个是控制websocket的方法
 */
const app = getApp()
//消息状态的枚举

const MESSAGE_CODE = {
  //连接成功  
  CONNECTED : "01",
  //ping消息
  HEARTBEAT: "02",
  //发送成功
  SEND_OK: "10",
  //发送失败
  SEND_FAIL: "11",
  //收到消息
  RECEIVE: "12",
}


/**
 * param:{
 *  onOpen:打开的回调
 *  onError:错误的回调
 *  onMessage:收到消息（已经处理好类型、是否异常的消息，ping消息不会回调）
 *  onClose:关闭的回调
 * }
 */
function webSocketService(param){
  return {
    socketQueue:[],

    //初始化websocket
    init(){
      const self = this
      var api = app.globalData.webSocketApiUrl + "?wmpToken=" + wx.getStorageSync('wmpToken'); //+ "?wechatId=" + this.data.toWechatId;
      wx.connectSocket({
        url: api,
      })

      wx.onSocketOpen((res) => {
        console.log('WebSocket连接已打开！')
        this.socketOpened = true
        //如果队列中有消息需要重新发送（这些是在socket关闭的时候存着的）
        for (let x of this.socketQueue) {
          wx.sendSocketMessage({
            data: x
          })
        }
        this.socketQueue = []
        param && param.onOpen && param.onOpen(res);
      })

      wx.onSocketError(function (res) {
        // wx.showModal({
        //   content: '无法连接到私信服务器',
        //   showCancel: false,
        // })
        console.log('WebSocket连接打开失败，请检查！', res)
        param && param.onError && param.onError(res);
      })

      wx.onSocketMessage(function (res) {
        var response = JSON.parse(res.data)
        switch(response.code){

          //建立连接
          case MESSAGE_CODE.CONNECTED:{
            console.log('连接建立成功')
            break;
          }

          //心跳包（回复一个ping）
          case MESSAGE_CODE.HEARTBEAT:{
            console.log('收到心跳包')
            wx.sendSocketMessage({
              data: 'ping'
            })
            break;
          }

          //收到消息
          case MESSAGE_CODE.RECEIVE: {
            var newMsg = {
              toWechatId: response.data.toWechatId,
              fromWechatId: response.data.fromWechatId,
              type: response.data.type,
              message: response.data.message,
            }
            param && param.onMessage && param.onMessage(newMsg);
            break;
          }
          
          //发送成功
          case MESSAGE_CODE.SEND_OK:{
            var newMsg = {
              toWechatId: response.data.toWechatId,
              fromWechatId: response.data.fromWechatId,
              type: response.data.type,
              message: response.data.message,
            }
            param && param.onMessage && param.onMessage(newMsg);
            break;
          }

          //发送失败
          case MESSAGE_CODE.SEND_FAIL: {
            wx.showToast({
              title: '发送失败',
              icon: 'none',
              duration: 2000,
            })
            break;
          }

          default:{
            wx.showToast({
              title: '收到未知类型的消息：'+response.code,
              icon: 'none',
              duration: 2000,
            })
            break;
          }
        }
      })

      wx.onSocketClose(res => {
        console.log('WebSocket 已关闭！')
        this.socketOpened = false
        param && param.onClose && param.onClose(res);
      })
    },

    //调用websocket发送消息
    send(mystr) {
      // 如果websocket未连接，先把消息推入队列并开始初始化socket
      if (this.socketOpened) {
        wx.sendSocketMessage({
          data: mystr
        })
      }
      else {
        this.socketQueue.push(mystr)
        this.init()
      }
    },

    // 离开页面时关闭websocket连接并停止发送心跳包
    close(){
      wx.closeSocket()
    }
  }
} 
module.exports = webSocketService;

