  },
  properties: {
    count: String,   //父子传参
    fileName: String,
  },
  /**
   * 页面的初始数据
   */
  data: {
    imgs: [],
    count: 1,//限制图片数量
    ishide: false,
    picslist:[],
    imgUrl: app.globalData.imgUrl,
    originImgs:[],
    datalist:[],
    hosts:"",
    fileName:""
  },
  // 上传图片
  methods: {
    chooseImg: function (e) {
      var that = this;
      var imgs = this.data.imgs; 
      if (imgs.length >= 9) {
        this.setData({
          lenMore: 1
        });
        setTimeout(function () {
          that.setData({
            lenMore: 0
          });
        }, 2500);
        return false;
      }       
      wx.chooseImage({
        count: that.data.count - (that.data.picslist.length||0),
        sizeType: ['original', 'compressed'], // 可以指定是原图还是压缩图，默认二者都有
        sourceType: ['album', 'camera'], // 可以指定来源是相册还是相机，默认二者都有
        success: function (res) {
          console.log(that.data.picslist,'slist')
          // 返回选定照片的本地文件路径列表，tempFilePath可以作为img标签的src属性显示图片
          var tempFilePaths = res.tempFilePaths; 
          var imgs = that.data.imgs; 
          for (var i = 0; i < tempFilePaths.length; i++) {
            if (imgs.length > that.data.count - 1) {
              that.setData({
                imgs: imgs
              });
              return false;
            } else {
              imgs.push(tempFilePaths[i]); 
              if (that.data.datalist.length){ 
                if (that.data.datalist.length > that.data.count - 1) {
                  that.setData({
                    ishide: true
                  });
                }
              }else{ 
                if (that.data.imgs.length > that.data.count - 1) {
                  that.setData({
                    ishide: true
                  });
                }
              } 
             
            }
          }
          that.setData({
            imgs: imgs
          });
          that.uploadFile(imgs);
          that.triggerEvent("chooseImg");//触发回调
        }
      });
    },
    // 删除图片
    deleteImg: function (e) {
      var imgs = this.data.imgs;
      var picslist = this.data.picslist;
      var index = e.currentTarget.dataset.index;
      imgs.splice(index, 1);
      picslist.splice(index, 1);
      this.setData({
        imgs: imgs,
        picslist: picslist
      });
      this.datalist(); 
      console.log(this.data.picslist.length, 'del')    
      this.triggerEvent("deleteImg");//触发回调
    },

    // 删除原来图片
    deloriginImg: function (e) {
      var originImgs = this.data.originImgs; 
      var index = e.currentTarget.dataset.index;
      originImgs.splice(index, 1); 
      this.setData({
        originImgs: originImgs, 
      });
      this.datalist(); 
      
      this.triggerEvent("deloriginImg");//触发回调
    },

    // 预览图片
    previewImg: function (e) {
      //获取当前图片的下标
      var index = e.currentTarget.dataset.index;
      //所有图片
      var imgs = this.data.imgs;
      wx.previewImage({
        //当前显示图片
        current: imgs[index],
        //所有图片
        urls: imgs
      })
    },

    datalist:function(){
      var imglist=[] 
      if (this.data.picslist.length > 0 && this.data.originImgs.length>0){
        var imglist = this.data.originImgs.concat(this.data.picslist)
      } else if (this.data.picslist.length > 0 && this.data.originImgs.length<1){
        var imglist = this.data.picslist
       } else{
        var imglist = this.data.originImgs
       }

      this.setData({
        datalist: imglist
      })  
      console.log(this.data.datalist,'aaa')
      if (this.data.datalist.length < Number(this.data.count)-1) {
        this.setData({
          ishide: false
        });
      }


    },

    //上传图片
    uploadFile: function (pics) {
      var that = this;
      that.setData({
        picslist: []
      })      
      for (var i = 0; i < pics.length; i++) {
        wx.uploadFile({
          url: app.globalData.apiUrl + '/upload/' + that.data.fileName+'/fileUpload',
          header: {
            "wmpToken": wx.getStorageSync('wmpToken'),
          },
          filePath: pics[i],
          name: 'file',
          success: function (res) {  
            if (typeof res.data =="string"){
              var resdata = JSON.parse(res.data)
              }             
            if (resdata.code == '0000') { 
              var picdata = that.data.picslist.concat(resdata.data.path);
              that.setData({
                picslist: picdata
              })
              console.log(that.data.picslist.length)
              that.datalist();           
            }  
          },
          fail: function (res) {
            wx.showToast({
              icon: 'none',
              title: '图片上传失败',
            })
          }

        })
      }
    },
  },
 

})

// component/wx-index-list/wx-index-list.js
Component({
  /**
   * 组件的属性列表
   */
  properties: {
    data: {
      type: Object,
      value: {},
    },
    myCity: {
      type: String,
      value: "",
    },
    // 用于外部组件搜索使用
    search:{
      type:String,
      value:"",
      observer: function (newVal, oldVal) { 
        console.log(newVal)
        this.value = newVal;
        this.searchMt();
      }
    }
  },

  data: {
    list: [],
    rightArr: [],// 右侧字母展示
    jumpNum: '',//跳转到那个字母
    myCityName: '请选择' // 默认我的城市

  },
  ready() {
    let data = this.data.data;
    this.resetRight(data);
    // if (this.data.myCity) {
    //   this.getCity()
    // }
  },
  methods: {
    // 数据重新渲染
    resetRight(data) {  
      let rightArr = []
      for (let i in data) {
        rightArr.push(data[i].title.substr(0, 1));
      }
      this.setData({
        list: data,
        rightArr
      })
    },
    // getCity() {
    //   wx.getLocation({
    //     type: 'wgs84',
    //     success: function (res) { 
    //       this.latitude = res.latitude;
    //       this.longitude = res.longitude;  
    //     }
    //   })
    // },
    // 右侧字母点击事件
    jumpMt(e) {
      let jumpNum = e.currentTarget.dataset.id;
      this.setData({ jumpNum });
    },
    // 列表点击事件
    detailMt(e) {
      let detail = e.currentTarget.dataset.detail;
      
      let myEventOption = {
        bubbles: false,//事件是否冒泡
        composed: false,//事件是否可以穿越组件边界
        capturePhase: false //事件是否拥有捕获阶段
      } // 触发事件的选项
      this.triggerEvent('detail', detail, myEventOption)

    },
    // 获取搜索输入内容
    input(e) {
      this.value = e.detail.value;
    },
    // 基础搜索功能
    searchMt() {
      this._search();
    },
    _search(){ 
      let data = this.data.data;
      let newData = [];
      for (let i = 0; i < data.length; i++) {
        let itemArr = [];
        for (let j = 0; j < data[i].item.length; j++) {
          if (data[i].item[j].name.indexOf(this.value) > -1) {
            let itemJson = {};
            for (let k in data[i].item[j]) {
              itemJson[k] = data[i].item[j][k];
            }
            itemArr.push(itemJson);
          }
        }
        if (itemArr.length === 0) {
          continue;
        }
        newData.push({
          title: data[i].title,
          type: data[i].type ? data[i].type : "",
          item: itemArr
        })
      }
      this.resetRight(newData);
    },
    // 城市定位
    locationMt() {
      // TODO 暂时没有实现 定位自己的城市，需要引入第三方api
    }

  }
})
